insert into DSY_T_QUERY (Q_ID, Q_CODE, Q_TITLE, Q_TABLE, Q_SQL, MONEY_UNIT, Q_IS_RIGHT, Q_IS_PAGE, Q_IS_SUM, Q_IS_OPENRUN, Q_IS_BUTTON, Q_REMARK, CREATE_USER, CREATE_DATE, L_OP_DATE, Q_IS_TITLE, PARENT_ID, CSS_URL, JS_URL, PRINT_CONFIG, COLUMN_CONFIG, RIGHT_LEVEL, EXPORT_LEVEL, TABLES_ID, SORT_INDEX, TABLES_SHEET_NAME)
values ('DEBT_BDJH_XZZQ_YBXM', null, '地方政府新增一般债券需求项目表', 'SELECT SJAD.CODE PROVINCE_CODE,
       SJAD.NAME PROVINCE_NAME,
       BILL.BILL_ID,
       BILL.BILL_YEAR,
       BILL.BATCH_NO,
       FXPC.NAME BATCH_NAME,
       AD.NAME AD_NAME,
       XM.AD_CODE,
       XM.XM_ID,
       AG.NAME AG_NAME,
       XM.XM_NAME,
       CONCAT(XMLX.CODE,XMLX.NAME) XMLX_NAME,
       CYLX.NAME CYLX_NAME,
       ZJYT.NAME XMXZ_NAME,
       XMJSZT.NAME JSZT_NAME,
       XM.LX_YEAR,
       TZJH.JSNX,
       XM.START_DATE_ACTUAL,
       XM.END_DATE_ACTUAL,
       XM.USE_UNIT_ID,
       XM.BILL_PERSON,
       XM.BILL_PHONE,
       BMFL.CODE || BMFL.NAME ZGBM_NAME,
       XM.XM_CODE,
       XM.FGW_XMK_CODE,
       NVL(XM.XMZGS_AMT, 0) XMZGS_AMT,
       CASE XM.BCXX1 WHEN 0 THEN ''否'' ELSE ''是'' END SF_LXSP,
       CASE XM.BCXX2 WHEN 0 THEN ''否'' ELSE ''是'' END SF_HPGZ,
       CASE XM.BCXX3 WHEN 0 THEN ''否'' ELSE ''是'' END SF_APGZ,
       CASE XM.BCXX4 WHEN 0 THEN ''否'' ELSE ''是'' END SF_ZTBGZ,
       CASE XM.BCXX5 WHEN 0 THEN ''否'' ELSE ''是'' END SF_ZDCQ,
       CASE XM.BCXX6 WHEN 0 THEN ''否'' ELSE ''是'' END SF_SWGZL,
       CASE XM.BCXX7 WHEN 0 THEN ''否'' ELSE ''是'' END SF_TZZC,
       SPYJ.NAME LXSPYJ_NAME,
       SPJC.NAME SPJC_NAME,
       CASE WHEN XM.UPPER_XM_ID IS NULL OR SJXM.LEVELNO = 1 THEN ''否'' ELSE ''是'' END AS IS_SJXM,
       CASE WHEN XM.UPPER_XM_ID IS NULL OR SJXM.LEVELNO = 1 THEN '''' ELSE SJXM.NAME END AS SJXM_NAME,
       CASE XM.BCXX8 WHEN 1 THEN ''一级'' WHEN 2 THEN ''二级'' WHEN 3 THEN ''三级'' WHEN 4 THEN ''四级'' ELSE ''五级'' END YXDJ,
       NVL(TZJH.HJ_PLAN_AMT, 0) HJ_PLAN_AMT,
       NVL(TZJH.CZYS_PLAN_AMT, 0) CZYS_PLAN_AMT,
       NVL(TZJH.ZWZJ_PLAN_AMT, 0) ZWZJ_PLAN_AMT,
       NVL(TZJH.CZYS_ACTUAL_AMT, 0) + NVL(XM_YRZ.ZWZJ_ACTUAL_AMT,0) HJ_ACTUAL_AMT,
       NVL(TZJH.CZYS_ACTUAL_AMT, 0) CZYS_ACTUAL_AMT,
       NVL(XM_YRZ.ZWZJ_ACTUAL_AMT,0) ZWZJ_ACTUAL_AMT,
       NVL(TZJH.BNHJ_PLAN_AMT, 0) BNHJ_PLAN_AMT,
       NVL(TZJH.BNCZYS_PLAN_AMT, 0) BNCZYS_PLAN_AMT,
       NVL(TZJH.BNZWZJ_PLAN_AMT, 0) BNZWZJ_PLAN_AMT,
       NVL(BILL.APPLY_AMOUNT, 0) APPLY_AMOUNT,
       ZQQX.NAME ZQQX_NAME,
       XM.REMARK REMARK,
       '''' CZB_STATUS,
       '''' FGW_STATUS,
       '''' CHECK_DATE,
       '''' CHECK_REMARK
  FROM (SELECT ZQ.ID BILL_ID,ZQ.BILL_YEAR,ZQ.BATCH_NO,ZQ.AD_CODE,ZQ.XM_ID,ZQ.ZQQX_ID,SUM(APPLY_AMOUNT1) APPLY_AMOUNT
          FROM DEBT_T_ZQGL_BILL ZQ
         WHERE IS_FXJH = ''0''
           AND BUSI_TYPE = ''0''
           AND JHGL_END = ''1''
           AND BOND_TYPE_ID LIKE  ''01%''
           AND SBZT <> ''0''
           AND BILL_YEAR = ''${SET_YEAR}''
           AND IS_VALID = ''1''
        ${IF(LEN(SBPC) == 0,
                   "",
                   " AND SBBATCH_NO IN (''" + REPLACE(SBPC, ",", "'',''") + "'')") }
         '||TO_CLOB('GROUP BY ZQ.ID,ZQ.BILL_YEAR,ZQ.BATCH_NO,ZQ.AD_CODE,ZQ.XM_ID,ZQ.ZQQX_ID ORDER BY ZQ.AD_CODE) BILL
  LEFT JOIN DEBT_T_PROJECT_INFO XM
    ON BILL.XM_ID = XM.XM_ID
  LEFT JOIN DSY_V_ELE_AD SJAD ON SJAD.LEVELNO = ''2''
  LEFT JOIN DSY_V_ELE_AD AD
    ON XM.AD_CODE = AD.CODE
  LEFT JOIN DSY_V_ELE_AG AG
    ON XM.AG_ID = AG.GUID
  LEFT JOIN DSY_V_ELE_BMFL BMFL
    ON AG.BMFL = BMFL.GUID
  LEFT JOIN DSY_V_ELE_XMJSZT XMJSZT
    ON XM.BUILD_STATUS_ID = XMJSZT.GUID
  LEFT JOIN DSY_V_ELE_ZWXMLX XMLX
    ON XM.XMLX_ID = XMLX.GUID
  LEFT JOIN DSY_V_ELE_ZJYT ZJYT
    ON XM.XMXZ_ID = ZJYT.GUID
  LEFT JOIN DSY_V_ELE_XMJSZT JSZT
    ON XM.BUILD_STATUS_ID = JSZT.GUID
  LEFT JOIN DSY_V_ELE_XMSPYJ SPYJ
    ON XM.LXSPYJ_ID = SPYJ.GUID
  LEFT JOIN DSY_V_ELE_XMSPJC SPJC
    ON XM.SP_LEVEL_ID = SPJC.GUID
  LEFT JOIN DSY_V_ELE_BDBCYLX CYLX
    ON CYLX.GUID = XM.CYLX_ID
  LEFT JOIN DSY_V_ELE_ZQQX ZQQX ON BILL.ZQQX_ID = ZQQX.GUID
  LEFT JOIN DSY_V_ELE_FXPC FXPC ON BILL.BATCH_NO = FXPC.GUID
  LEFT JOIN (SELECT T.XM_ID,MAX(ND)-MIN(ND) JSNX,
             SUM(T.SJBZ_PLAN_AMT + T.CZYS_PLAN_AMT + T.RZZJ_PLAN_AMT) HJ_PLAN_AMT,
             SUM(T.SJBZ_PLAN_AMT + T.CZYS_PLAN_AMT) CZYS_PLAN_AMT,
             SUM(T.RZZJ_PLAN_AMT) ZWZJ_PLAN_AMT,
             SUM(T.SJBZ_ACTUAL_AMT + T.CZYS_ACTUAL_AMT) CZYS_ACTUAL_AMT,
             SUM(CASE WHEN T.ND = ''${SET_YEAR}'' THEN T.SJBZ_PLAN_AMT + T.CZYS_PLAN_AMT + T.RZZJ_PLAN_AMT ELSE 0 END) BNHJ_PLAN_AMT,
             SUM(CASE WHEN T.ND = ''${SET_YEAR}'' THEN T.SJBZ_PLAN_AMT + T.CZYS_PLAN_AMT ELSE 0 END) BNCZYS_PLAN_AMT,
             SUM(CASE WHEN T.ND = ''${SET_YEAR}'' THEN T.RZZJ_PLAN_AMT ELSE 0 END) BNZWZJ_PLAN_AMT
        FROM DEBT_T_ZQGL_XM_PLAN T
       GROUP BY T.XM_ID) TZJH ON XM.XM_ID = TZJH.XM_ID
  LEFT JOIN (SELECT XM_ID, SUM(ZWZJ_AMT) ZWZJ_ACTUAL_AMT
               FROM (SELECT ZCXX.XM_ID, ZCXX.PAY_AMT ZWZJ_AMT
                       FROM DEBT_T_ZQGL_ZCXX ZCXX
                      WHERE ZCXX.ZC_TYPE = ''0''
                     UNION ALL
                     SELECT ZWXX.XM_ID, CHBJ.CHBJ_AMT ZWZJ_AMT
                       FROM DEBT_T_ZWGL_CHBJ CHBJ
                       LEFT JOIN DEBT_T_ZWGL_ZWXX ZWXX
                         ON ZWXX.ZW_ID = CHBJ.ZW_ID
                      WHERE CHBJ.ZJLY_ID LIKE ''03%'')
              GROUP BY XM_ID) XM_YRZ
    ON XM.XM_ID = XM_YRZ.XM_ID
  LEFT JOIN DSY_V_ELE_SJXM SJXM
    ON SJXM.GUID = (CASE
         WHEN INSTR(XM.UPPER_XM_ID, '' '', 1, 1) > 0 THEN
          SUBSTR(XM.UPPER_XM_ID, 1, INSTR(XM.UPPER_XM_ID, '' '', 1, 1) - 1)
         ELSE
          XM.UPPER_XM_ID
       END)'), null, 10000, 1, 1, 1, 1, 1, null, '21admin', '2020-08-28 16:11:35', '2020-08-29 16:58:37', 1, '7B2B263AA7624C86992EA189808B9FDB', null, '/page/plat/query/template/js/reportRownumber.js', null, null, 1, 2, 'DEBT_BDJH_XZZQ_XQXM', 1, '地方政府新增一般债券需求项目表');
