  FROM (SELECT ZQ.ID BILL_ID,ZQ.BILL_YEAR,ZQ.BATCH_NO,ZQ.AD_CODE,ZQ.XM_ID,ZQ.ZQQX_ID,SUM(APPLY_AMOUNT1) APPLY_AMOUNT,SUM(NVL(ZQ.ZBJ_AMT,0)) ZBJ_AMT
          FROM DEBT_T_ZQGL_BILL ZQ
         WHERE IS_FXJH = ''0''
           AND BUSI_TYPE = ''0''
           AND JHGL_END = ''1''
           AND BOND_TYPE_ID LIKE  ''02%''
           AND SBZT <> ''0''
           AND BILL_YEAR = ''${SET_YEAR}''
           AND IS_VALID = ''1''
         '||TO_CLOB('GROUP BY ZQ.ID,ZQ.BILL_YEAR,ZQ.BATCH_NO,ZQ.AD_CODE,ZQ.XM_ID,ZQ.ZQQX_ID) BILL
  LEFT JOIN DEBT_T_PROJECT_INFO XM
    ON BILL.XM_ID = XM.XM_ID
  LEFT JOIN DSY_V_ELE_AD SJAD ON SJAD.LEVELNO = ''2''
  LEFT JOIN DSY_V_ELE_AD AD
    ON XM.AD_CODE = AD.CODE
  LEFT JOIN DSY_V_ELE_AG AG
    ON XM.AG_ID = AG.GUID
  LEFT JOIN DSY_V_ELE_BMFL BMFL
    ON AG.BMFL = BMFL.GUID
  LEFT JOIN DSY_V_ELE_XMJSZT XMJSZT
    ON XM.BUILD_STATUS_ID = XMJSZT.GUID
  LEFT JOIN DSY_V_ELE_ZWXMLX XMLX
    ON XM.XMLX_ID = XMLX.GUID
  LEFT JOIN DSY_V_ELE_ZJYT ZJYT
    ON XM.XMXZ_ID = ZJYT.GUID
  LEFT JOIN DSY_V_ELE_XMJSZT JSZT
    ON XM.BUILD_STATUS_ID = JSZT.GUID
  LEFT JOIN DSY_V_ELE_XMSPYJ SPYJ
    ON XM.LXSPYJ_ID = SPYJ.GUID
  LEFT JOIN DSY_V_ELE_XMSPJC SPJC
    ON XM.SP_LEVEL_ID = SPJC.GUID
  LEFT JOIN DSY_V_ELE_BDBCYLX CYLX
    ON CYLX.GUID = XM.CYLX_ID
  LEFT JOIN DSY_V_ELE_ZQQX ZQQX ON BILL.ZQQX_ID = ZQQX.GUID
  LEFT JOIN DSY_V_ELE_FXPC FXPC ON BILL.BATCH_NO = FXPC.GUID
  LEFT JOIN (SELECT T.XM_ID,MAX(ND)-MIN(ND) JSNX,
             SUM(T.SJBZ_PLAN_AMT + T.CZYS_PLAN_AMT + T.RZZJ_PLAN_AMT) HJ_PLAN_AMT,
             SUM(T.SJBZ_PLAN_AMT + T.CZYS_PLAN_AMT) CZYS_PLAN_AMT,
             SUM(T.RZZJ_PLAN_AMT) ZWZJ_PLAN_AMT,
