             SUM(CASE WHEN T.ND = ''${SET_YEAR}'' THEN T.SJBZ_PLAN_AMT + T.CZYS_PLAN_AMT + T.RZZJ_PLAN_AMT ELSE 0 END) BNHJ_PLAN_AMT,
