                       FROM DEBT_T_ZWGL_CHBJ CHBJ
                       LEFT JOIN DEBT_T_ZWGL_ZWXX ZWXX
                         ON ZWXX.ZW_ID = CHBJ.ZW_ID
                      WHERE CHBJ.ZJLY_ID LIKE ''03%'')
              GROUP BY XM_ID) XM_YRZ
    ON XM.XM_ID = XM_YRZ.XM_ID
  LEFT JOIN (SELECT SUM(XMSR.ZFXJJ_AMT + XMSR.CZBZ_AMT + XMSR.QT_AMT +
                        XMSR.WNRYS_AMT) XMZSY,
                    XMSR.XM_ID
               FROM DEBT_T_ZQGL_XM_INCOME XMSR
              WHERE XMSR.INCOME_YEAR = ''${SET_YEAR}''
              GROUP BY XMSR.XM_ID) XMSY
    ON XM.XM_ID = XMSY.XM_ID
  LEFT JOIN (SELECT XMZC.XM_ID,
                    SUM(XMZC_DTL.ZCJZ_AMT) ZCJZ_AMT,
                    SUM(XMZC_DTL.ZJYZ_AMT) ZCYZ_AMT,
                    SUM(XMZC_DTL.LJSY_AMT) LJSY_AMT,
                    SUM(XMZC_DTL.CZSR_AMT) CZSR_AMT,
                    SUM(XMZC_DTL.DYDB_AMT) DYDB_AMT,
                    XMZC.REMARK
               FROM DEBT_T_ZCGL_XMZC_DTL XMZC_DTL
               LEFT JOIN DEBT_T_ZCGL_XMZC XMZC
                 ON XMZC_DTL.XMZC_ID = XMZC.XMZC_ID
               LEFT JOIN DEBT_T_ZCGL_XMZC_HZ ZCHZ
                 ON ZCHZ.HZD_TYPE = 0
                AND ZCHZ.HZD_ID = XMZC.DWHZ_ID
               LEFT JOIN DSY_V_ELE_SBQJ SBQJ
                 ON SBQJ.GUID = ZCHZ.SBQJ_ID
              WHERE SBQJ.YEAR = ''${SET_YEAR}'' - 1
