values ('DEBT_BDJH_ZRZYB_XQMX', null, '地方政府再融资一般债券需求明细表', 'SELECT DQZQ.AD AD_CODE,
       AD.NAME AD_NAME,
       DQZQ.BILL_YEAR,
       DQZQ.BATCH_NO,
       DQZQ.ZQ_ID,
       ZQ.ZQ_CODE,
       ZQ.ZQ_NAME,
       ZQ.FX_START_DATE FX_DATE,
       ZQQX.NAME ZQQX_NAME,
       FXFS.NAME FXFS_NAME,
       ZQLB.NAME ZQLB_NAME,
       ZQ.FX_AMT ZQ_AMT,
       ZQ.DQDF_DATE,
       ZQ.FX_AMT DQ_AMT,
       DQZQ.SQ_ZRZ_AMT,
       '''' SH_AMT,
       '''' SH_DATE,
       '''' REMARK
 FROM (SELECT SUBSTR(BILL.AD_CODE, 1, 2) AD,
       BILL.BILL_YEAR,
       BILL.BATCH_NO,
       DTL.ZQ_ID,
       SUM(NVL(DTL.APPLY_AMT, 0)) SQ_ZRZ_AMT
  FROM DEBT_T_ZQGL_JXHJ_BILL_DTL DTL
  LEFT JOIN DEBT_T_ZQGL_JXHJ_BILL BILL
    ON BILL.BILL_ID = DTL.BILL_ID
  LEFT JOIN DEBT_T_ZQGL_ZCXX ZCXX ON DTL.ZC_ID = ZCXX.ZC_ID
 WHERE BILL.JHGL_END = ''1''
   AND DTL.SBZT <> ''0''
   AND ZCXX.ZC_TYPE = ''0''
   AND BILL.BOND_TYPE_ID LIKE ''01%''
   AND DTL.DATA_TYPE = ''ZQ''
 GROUP BY SUBSTR(BILL.AD_CODE, 1, 2),
          BILL.BILL_YEAR,
          BILL.BATCH_NO,
          DTL.ZQ_ID) DQZQ
LEFT JOIN DEBT_T_ZQGL_ZQXX ZQ ON DQZQ.ZQ_ID = ZQ.ZQ_ID
LEFT JOIN DSY_V_ELE_ZQQX ZQQX ON ZQ.ZQQX_ID = ZQQX.GUID
LEFT JOIN DSY_V_ELE_FXFS FXFS ON ZQ.FXFS_ID = FXFS.GUID
LEFT JOIN DSY_V_ELE_ZQLB ZQLB ON ZQ.ZQLB_ID = ZQLB.GUID
LEFT JOIN DSY_V_ELE_AD AD ON DQZQ.AD = AD.CODE
WHERE 1=1
UNION ALL
SELECT DQZW.AD AD_CODE,
